                            lineNum: leftIndex + 1,
                            content: originalLines[leftIndex],
                            type: 'removed'
                        });
                        result.right.push({
                            lineNum: null,
                            content: '',
                            type: 'empty'
                        });
                        result.arrows.push({ type: 'left' });
                        leftIndex++;
                        arrowIndex++;
                        result.stats.removed++;
                    }
                } else if (change.type === 'insert') {
                    // Added lines
                    for (let i = 0; i < change.count; i++) {
                        result.left.push({
                            lineNum: null,
                            content: '',
                            type: 'empty'
                        });
                        result.right.push({
                            lineNum: rightIndex + 1,
                            content: modifiedLines[rightIndex],
                            type: 'added'
                        });
                        result.arrows.push({ type: 'right' });
                        rightIndex++;
                        arrowIndex++;
                        result.stats.added++;
                    }
                }
            }

            // Check for modified lines (similar but different)
            for (let i = 0; i < Math.min(result.left.length, result.right.length); i++) {
                const leftLine = result.left[i];
                const rightLine = result.right[i];

                if (leftLine.type === 'removed' && rightLine.type === 'added') {
                    // Check similarity - if > 50% similar, treat as modified
                    const similarity = calculateSimilarity(leftLine.content, rightLine.content);
                    if (similarity > 0.5) {
                        leftLine.type = 'modified';
                        rightLine.type = 'modified';
                        result.arrows[i] = { type: 'both' };
                        result.stats.modified++;
                        result.stats.added--;
                        result.stats.removed--;
