        // Myers Diff Algorithm (simplified implementation)
        function myersDiff(a, b) {
            const n = a.length;
            const m = b.length;
            const max = n + m;
            const v = {};
            const trace = [];

            v[1] = 0;

            for (let d = 0; d <= max; d++) {
                trace.push({ ...v });

                for (let k = -d; k <= d; k += 2) {
                    let x;
                    if (k === -d || (k !== d && v[k - 1] < v[k + 1])) {
                        x = v[k + 1];
                    } else {
                        x = v[k - 1] + 1;
                    }

                    let y = x - k;

                    while (x < n && y < m && a[x] === b[y]) {
                        x++;
                        y++;
                    }

                    v[k] = x;

                    if (x >= n && y >= m) {
                        return buildDiff(a, b, trace, d);
                    }
                }
            }

            return [];
        }

        function buildDiff(a, b, trace, d) {
            const diff = [];
            let x = a.length;
            let y = b.length;

            for (let depth = d; depth > 0; depth--) {
                const v = trace[depth];
                const k = x - y;
                let prevK;

                if (k === -depth || (k !== depth && v[k - 1] < v[k + 1])) {
                    prevK = k + 1;
                } else {
                    prevK = k - 1;
                }

                const prevX = v[prevK];
                const prevY = prevX - prevK;

                while (x > prevX && y > prevY) {
                    diff.unshift({ type: 'equal', count: 1 });
                    x--;
                    y--;
                }

                if (depth > 0) {
                    if (x > prevX) {
                        diff.unshift({ type: 'delete', count: 1 });
                        x--;
                    } else {
                        diff.unshift({ type: 'insert', count: 1 });
                        y--;
                    }
                }
            }

            return diff;
        }

        function calculateSimilarity(str1, str2) {
            const len1 = str1.length;
            const len2 = str2.length;

            if (len1 === 0 && len2 === 0) return 1;
            if (len1 === 0 || len2 === 0) return 0;

            // Simple character-based similarity
            const maxLen = Math.max(len1, len2);
            let matches = 0;

            for (let i = 0; i < Math.min(len1, len2); i++) {
                if (str1[i] === str2[i]) {
                    matches++;
                }
            }

            return matches / maxLen;
        }

