        function buildDiff(a, b, trace, d) {
            const diff = [];
            let x = a.length;
            let y = b.length;

            for (let depth = d; depth > 0; depth--) {
                const v = trace[depth];
                const k = x - y;
                let prevK;

                if (k === -depth || (k !== depth && v[k - 1] < v[k + 1])) {
                    prevK = k + 1;
                } else {
                    prevK = k - 1;
                }

                const prevX = v[prevK];
                const prevY = prevX - prevK;

                while (x > prevX && y > prevY) {
                    diff.unshift({ type: 'equal', count: 1 });
                    x--;
                    y--;
                }

                if (depth > 0) {
                    if (x > prevX) {
                        diff.unshift({ type: 'delete', count: 1 });
                        x--;
                    } else {
                        diff.unshift({ type: 'insert', count: 1 });
