    <script>
        // Clean state management
        const state = {
            loadedDocument: '',
            documentLines: [],
            fileName: '',
            detectedRange: null,
            diffResult: null,
            changes: []
        };

        // Marker patterns
        const markers = {
            '1/8': /^#â…›â…›â…›â…›â…›â…› 1\/8 MARKER â…›â…›â…›â…›â…›#/,
            '1/4': /^#Â¼Â¼Â¼Â¼Â¼Â¼ 1\/4 MARKER Â¼Â¼Â¼Â¼Â¼#/,
            '3/8': /^#â…œâ…œâ…œâ…œâ…œâ…œ 3\/8 MARKER â…œâ…œâ…œâ…œâ…œ#/,
            '1/2': /^#Â½Â½Â½Â½Â½Â½ 1\/2 MARKER Â½Â½Â½Â½Â½#/,
            '5/8': /^#â…â…â…â…â…â… 5\/8 MARKER â…â…â…â…â…#/,
            '3/4': /^#Â¾Â¾Â¾Â¾Â¾Â¾ 3\/4 MARKER Â¾Â¾Â¾Â¾Â¾#/,
            '7/8': /^#â…â…â…â…â…â… 7\/8 MARKER â…â…â…â…â…#/
        };

        // Initialize when DOM is ready
        document.addEventListener('DOMContentLoaded', function() {
            console.log('ğŸš€ Marker Diff Manager - Clean Build Starting...');
            setupEventListeners();
        });

        function setupEventListeners() {
            const fileInput = document.getElementById('fileInput');
            const loadBtn = document.getElementById('loadBtn');
            const pasteArea = document.getElementById('pasteArea');
            const analyzeBtn = document.getElementById('analyzeBtn');
            const applyAllBtn = document.getElementById('applyAllBtn');
            const rejectAllBtn = document.getElementById('rejectAllBtn');
            const downloadBtn = document.getElementById('downloadBtn');

            fileInput.addEventListener('change', function() {
                loadBtn.disabled = !this.files.length;
                console.log('ğŸ“ File selected:', this.files[0]?.name);
            });

            loadBtn.addEventListener('click', loadDocument);
            analyzeBtn.addEventListener('click', analyzeChanges);
            applyAllBtn.addEventListener('click', applyAllChanges);
            rejectAllBtn.addEventListener('click', rejectAllChanges);
            downloadBtn.addEventListener('click', downloadUpdatedFile);

            pasteArea.addEventListener('input', function() {
                updateAnalyzeButton();
            });

            console.log('âœ… Event listeners attached');
        }

        function updateAnalyzeButton() {
            const pasteArea = document.getElementById('pasteArea');
            const analyzeBtn = document.getElementById('analyzeBtn');

            const hasText = pasteArea.value.trim().length > 0;
            const hasDoc = state.loadedDocument.length > 0;

            analyzeBtn.disabled = !hasText || !hasDoc;
        }

        function loadDocument() {
            console.log('ğŸ“– Loading document...');
            const fileInput = document.getElementById('fileInput');
            const file = fileInput.files[0];

            if (!file) return;

            state.fileName = file.name;
            const reader = new FileReader();

            reader.onload = function(e) {
                state.loadedDocument = e.target.result;
                state.documentLines = state.loadedDocument.split('\n');

                console.log(`âœ… Document loaded: ${state.fileName} (${state.documentLines.length} lines)`);
                showMessage(`âœ… Loaded "${state.fileName}" - ${state.documentLines.length} lines`, 'success');
                updateAnalyzeButton();
            };

            reader.onerror = function() {
                console.error('âŒ File read error');
                showMessage('âŒ Error reading file', 'error');
            };

            reader.readAsText(file);
        }

        function analyzeChanges() {
            console.log('ğŸ” Analyzing changes...');
            const pasteArea = document.getElementById('pasteArea');
            const pastedText = pasteArea.value.trim();

            if (!pastedText || !state.loadedDocument) {
                showMessage('âŒ Missing pasted text or loaded document', 'error');
                return;
            }

            // Step 1: Detect marker range
            state.detectedRange = detectMarkerRange(pastedText);
            if (!state.detectedRange) {
                showMessage('âŒ No valid marker range detected', 'error');
                return;
            }

            console.log('ğŸ¯ Detected range:', state.detectedRange);
            showMarkerInfo();

            // Step 2: Find markers in document
            const docStartLine = findMarkerInDocument(state.detectedRange.startMarker);
            const docEndLine = findMarkerInDocument(state.detectedRange.endMarker);

            if (docStartLine === -1 || docEndLine === -1) {
                showMessage('âŒ Could not find matching markers in loaded document', 'error');
                return;
            }

            console.log('ğŸ“ Found markers at lines:', docStartLine, docEndLine);

            // Step 3: Extract content and compute diff
            const originalContent = state.documentLines.slice(docStartLine + 1, docEndLine).join('\n');
            const newContent = state.detectedRange.content;

            state.diffResult = computeDiff(originalContent, newContent);
            console.log('ğŸ”„ Diff computed:', state.diffResult.stats);

            // Step 4: Display results
            displayDiff();
            displayStats();
            displayChanges();
            showPanels();

            showMessage('ğŸ¯ Analysis complete! Review changes below.', 'success');
        }

        function detectMarkerRange(text) {
            const lines = text.trim().split('\n');
            let startMarker = null;
            let endMarker = null;
            let startLine = -1;
            let endLine = -1;

            for (let i = 0; i < lines.length; i++) {
                for (const [fraction, pattern] of Object.entries(markers)) {
                    if (pattern.test(lines[i].trim())) {
                        if (!startMarker) {
                            startMarker = fraction;
                            startLine = i;
                        } else {
                            endMarker = fraction;
                            endLine = i;
                            break;
                        }
                    }
                }
                if (endMarker) break;
            }

            if (startMarker && endMarker) {
                return {
                    startMarker,
                    endMarker,
                    startLine,
                    endLine,
                    content: lines.slice(startLine + 1, endLine).join('\n')
                };
            }

            return null;
        }

        function findMarkerInDocument(marker) {
            const pattern = markers[marker];
            for (let i = 0; i < state.documentLines.length; i++) {
                if (pattern.test(state.documentLines[i].trim())) {
                    return i;
                }
            }
            return -1;
        }

        function computeDiff(original, modified) {
            const originalLines = original.split('\n');
            const modifiedLines = modified.split('\n');

            const result = {
                left: [],
                right: [],
                arrows: [],
                stats: { added: 0, removed: 0, modified: 0, total: 0 }
            };

            state.changes = [];
            let changeId = 0;

            const maxLines = Math.max(originalLines.length, modifiedLines.length);

            for (let i = 0; i < maxLines; i++) {
                const origLine = originalLines[i];
                const modLine = modifiedLines[i];

                if (origLine === undefined) {
                    // Added line
                    result.left.push({ lineNum: null, content: '', type: 'empty' });
                    result.right.push({ lineNum: i + 1, content: modLine, type: 'added' });
                    result.arrows.push({ type: 'right' });
                    result.stats.added++;

                    state.changes.push({
                        id: changeId++,
                        type: 'added',
                        lineNum: i + 1,
                        content: modLine,
                        status: 'pending'
                    });

                } else if (modLine === undefined) {
                    // Removed line
                    result.left.push({ lineNum: i + 1, content: origLine, type: 'removed' });
                    result.right.push({ lineNum: null, content: '', type: 'empty' });
                    result.arrows.push({ type: 'left' });
                    result.stats.removed++;

                    state.changes.push({
                        id: changeId++,
                        type: 'removed',
                        lineNum: i + 1,
                        content: origLine,
                        status: 'pending'
                    });

                } else if (origLine !== modLine) {
                    // Modified line
                    result.left.push({ lineNum: i + 1, content: origLine, type: 'modified' });
                    result.right.push({ lineNum: i + 1, content: modLine, type: 'modified' });
                    result.arrows.push({ type: 'both' });
                    result.stats.modified++;

                    state.changes.push({
                        id: changeId++,
                        type: 'modified',
                        lineNum: i + 1,
                        originalContent: origLine,
                        newContent: modLine,
                        status: 'pending'
                    });

                } else {
                    // Unchanged line
                    result.left.push({ lineNum: i + 1, content: origLine, type: 'unchanged' });
                    result.right.push({ lineNum: i + 1, content: modLine, type: 'unchanged' });
                    result.arrows.push({ type: 'none' });
                }
            }

            result.stats.total = result.stats.added + result.stats.removed + result.stats.modified;
            return result;
        }

        function showMarkerInfo() {
            document.getElementById('startMarker').textContent = state.detectedRange.startMarker;
            document.getElementById('endMarker').textContent = state.detectedRange.endMarker;
            document.getElementById('sectionInfo').textContent = 
                `Lines ${state.detectedRange.startLine + 1} to ${state.detectedRange.endLine + 1}`;
            document.getElementById('markerInfo').style.display = 'block';
        }

        function displayDiff() {
            const leftContent = document.getElementById('leftContent');
            const rightContent = document.getElementById('rightContent');
            const arrowContent = document.getElementById('arrowContent');

            leftContent.innerHTML = '';
            rightContent.innerHTML = '';
            arrowContent.innerHTML = '';

            for (let i = 0; i < state.diffResult.left.length; i++) {
                const leftLine = state.diffResult.left[i];
                const rightLine = state.diffResult.right[i];
                const arrow = state.diffResult.arrows[i];

                // Left panel
                const leftDiv = document.createElement('div');
                leftDiv.className = `diff-line line-${leftLine.type}`;
                leftDiv.innerHTML = `
                    <div class="line-number">${leftLine.lineNum || ''}</div>
                    <div class="line-content">${escapeHtml(leftLine.content)}</div>
                `;
                leftContent.appendChild(leftDiv);

                // Right panel
                const rightDiv = document.createElement('div');
                rightDiv.className = `diff-line line-${rightLine.type}`;
                rightDiv.innerHTML = `
                    <div class="line-number">${rightLine.lineNum || ''}</div>
                    <div class="line-content">${escapeHtml(rightLine.content)}</div>
                `;
                rightContent.appendChild(rightDiv);

                // Arrow
                const arrowDiv = document.createElement('div');
                arrowDiv.className = 'arrow-container';

                if (arrow.type === 'right') {
                    arrowDiv.innerHTML = '<div class="arrow-right"></div>';
                } else if (arrow.type === 'left') {
                    arrowDiv.innerHTML = '<div class="arrow-left"></div>';
                } else if (arrow.type === 'both') {
                    arrowDiv.innerHTML = '<div class="arrow-both"></div>';
                }

                arrowContent.appendChild(arrowDiv);
            }
        }

        function displayStats() {
            const stats = state.diffResult.stats;
            document.getElementById('linesAdded').textContent = stats.added;
            document.getElementById('linesRemoved').textContent = stats.removed;
            document.getElementById('linesModified').textContent = stats.modified;
            document.getElementById('totalChanges').textContent = stats.total;
        }

        function displayChanges() {
            const changesList = document.getElementById('changesList');

            if (state.changes.length === 0) {
                changesList.innerHTML = '<p style="color: #888;">No changes detected</p>';
                return;
            }

            changesList.innerHTML = state.changes.map(change => {
                let previewContent = '';

                if (change.type === 'added') {
                    previewContent = `<span style="color: #00ff00;">+ ${escapeHtml(change.content)}</span>`;
                } else if (change.type === 'removed') {
                    previewContent = `<span style="color: #ff6666;">- ${escapeHtml(change.content)}</span>`;
                } else if (change.type === 'modified') {
                    previewContent = `
                        <span style="color: #ff6666;">- ${escapeHtml(change.originalContent)}</span><br>
                        <span style="color: #00ff00;">+ ${escapeHtml(change.newContent)}</span>
                    `;
                }

                const statusClass = change.status === 'applied' ? 'applied' : 
                                   change.status === 'rejected' ? 'rejected' : '';

                return `
                    <div class="change-item ${statusClass}" id="change-${change.id}">
                        <div class="change-info">
                            <div>
                                <span class="change-type ${change.type}">${change.type.toUpperCase()}</span>
                                Line ${change.lineNum}
                            </div>
                            <div class="change-preview">${previewContent}</div>
                        </div>
                        <div class="change-actions">
                            <button class="btn btn-small btn-apply" onclick="applyChange(${change.id})">Apply</button>
                            <button class="btn btn-small btn-reject" onclick="rejectChange(${change.id})">Reject</button>
                        </div",
